public class Solution {    public String convert(String s, int nRows) {        if(s.length()<=nRows || nRows==1)	return s;        StringBuffer sb = new StringBuffer();	    if(s==null || s.equals(""))		    return sb.toString();			    int cycle = nRows*2-2;	    for(int i=0;i<nRows;i++){		    int index = i;		    for(int j=0;j>=0;j++){			    index = cycle*j+i;			    if(index>=s.length())		break;			    sb.append(s.substring(index, index+1));			    if(i!=0 && i!=nRows-1){			    	index = (j+1)*cycle-i;			    	if(index>=s.length())		break;			    	sb.append(s.substring(index, index+1));			    }		    }	    }	    return sb.toString();    }}